-- 1. Таблица курсов
CREATE TABLE IF NOT EXISTS library_courses (
  id UUID DEFAULT uuid_generate_v4() PRIMARY KEY,
  category VARCHAR(100) NOT NULL,
  title VARCHAR(255) NOT NULL,
  platform VARCHAR(100) NOT NULL,
  description TEXT,
  target_audience TEXT,
  language VARCHAR(50) DEFAULT 'Русский',
  duration VARCHAR(100),
  url TEXT NOT NULL,
  is_active BOOLEAN DEFAULT true,
  sort_order INTEGER DEFAULT 0,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- 2. Таблица грантов
CREATE TABLE IF NOT EXISTS library_grants (
  id UUID DEFAULT uuid_generate_v4() PRIMARY KEY,
  category VARCHAR(100) NOT NULL,
  title VARCHAR(255) NOT NULL,
  organizer VARCHAR(255) NOT NULL,
  support_type VARCHAR(100),
  amount VARCHAR(255),
  target_audience TEXT,
  description TEXT,
  deadline VARCHAR(255),
  url TEXT NOT NULL,
  is_active BOOLEAN DEFAULT true,
  sort_order INTEGER DEFAULT 0,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- 3. Таблица акселераторов
CREATE TABLE IF NOT EXISTS library_accelerators (
  id UUID DEFAULT uuid_generate_v4() PRIMARY KEY,
  category VARCHAR(100) NOT NULL,
  title VARCHAR(255) NOT NULL,
  organizer VARCHAR(255) NOT NULL,
  format VARCHAR(100),
  duration VARCHAR(100),
  language VARCHAR(100),
  benefits TEXT,
  target_audience TEXT,
  description TEXT,
  requirements TEXT,
  url TEXT NOT NULL,
  is_active BOOLEAN DEFAULT true,
  sort_order INTEGER DEFAULT 0,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- 4. Таблица избранного
CREATE TABLE IF NOT EXISTS library_favorites (
  id UUID DEFAULT uuid_generate_v4() PRIMARY KEY,
  user_id UUID NOT NULL,
  resource_type VARCHAR(50) NOT NULL,
  resource_id UUID NOT NULL,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  UNIQUE(user_id, resource_type, resource_id)
);

-- Создание индексов
CREATE INDEX IF NOT EXISTS idx_courses_category ON library_courses(category);
CREATE INDEX IF NOT EXISTS idx_grants_category ON library_grants(category);
CREATE INDEX IF NOT EXISTS idx_accelerators_category ON library_accelerators(category);
